import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Button,
  Container,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  IconButton,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import api from "../api/api";

export default function ListarClientes() {
  const [clientes, setClientes] = useState([]);
  const navigate = useNavigate();

  const carregarClientes = async () => {
    const response = await api.get("/clientes");
    setClientes(response.data);
  };

  const deletarCliente = async (id) => {
    await api.delete(`/clientes/${id}`);
    carregarClientes();
  };

  useEffect(() => {
    carregarClientes();
  }, []);

  return (
    <Container>
      <Typography variant="h4" gutterBottom>Lista de Clientes</Typography>
      <Button variant="contained" color="primary" onClick={() => navigate("/novo")}>
        Novo
      </Button>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>ID</TableCell>
            <TableCell>Nome</TableCell>
            <TableCell>CNPJ</TableCell>
            <TableCell>Segmento</TableCell>
            <TableCell>Ações</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {clientes.map((cli) => (
            <TableRow key={cli.id}>
              <TableCell>{cli.id}</TableCell>
              <TableCell>{cli.nome}</TableCell>
              <TableCell>{cli.cnpj}</TableCell>
              <TableCell>{cli.segmento}</TableCell>
              <TableCell>
                <IconButton color="error" onClick={() => deletarCliente(cli.id)}>
                  <DeleteIcon />
                </IconButton>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </Container>
  );
}
